<html lang="en" data-theme="light">

<head>
		<meta charset="UTF-8">
		<meta name="viewport" content="width=device-width, initial-scale=1">
		<title>Smart SandBox</title>
		<script type="text/javascript">
				// creates a holding array for the list folders
				var list = [];

				/**
				 * Search at root level for any folder and loops through each 
				 * folder converting all characters to lower case before pushing
				 * to the list array - if you get errors here make sure that your 
				 * server has the right access permissions to the root folder
				 */
				<?php $folders = glob("*"); foreach ($folders as $folder){ if(is_dir($folder)){$folder = strtolower($folder); ?>
					var obj = {};
					obj.name = "<?php echo $folder ?>";
					obj.date = "<?php echo date ("ymd", filemtime($folder)); ?>";
					obj.datePreaty = "<?php echo date ("d-m-y", filemtime($folder)); ?>";
					list.push(obj);
				<?php }} ?>
		</script>
